import React, { useEffect, useState } from "react";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import Navbar from "../components/navbar";
import Footer from "../components/footer";
import Sidepannel from "../components/side-pannel";

function PatientForm() {
  const [patients, setPatients] = useState([]);

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await fetch("http://localhost:4600/api/auth/patient");
        const data = await response.json();
        setPatients(data);
      } catch (error) {
        console.error("Error fetching patients:", error);
      }
    };

    fetchPatients();
  }, []);

  const generatePatientReport = (patient) => {
    const doc = new jsPDF();
    doc.text(`Patient Report - ${patient.name}`, 14, 10);

    doc.autoTable({
      startY: 20,
      head: [["Field", "Details"]],
      body: [
        ["Name", patient.name],
        ["Phone Number", patient.phoneNumber],
        ["Blood Type", patient.blood],
        ["Profile Image", "Attached"], // Display a placeholder
      ],
    });

    doc.save(`Patient_Report_${patient.name}.pdf`);
  };

  return (
    <div className="layout-wrapper layout-content-navbar">
      <div className="layout-container">
        <Sidepannel />
        <div className="layout-page">
          <Navbar />
          <div className="content-wrapper">
            <div className="container-xxl flex-grow-1 container-p-y">
              <h4 className="fw-bold py-3 mb-4">Patient Records</h4>
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Phone Number</th>
                      <th>Blood Type</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {patients.map((patient) => (
                      <tr key={patient.id}>
                        <td>{patient.name}</td>
                        <td>{patient.phoneNumber}</td>
                        <td>{patient.blood}</td>
                        <td>
                          <button
                            className="btn btn-primary btn-sm"
                            onClick={() => generatePatientReport(patient)}
                          >
                            Generate Report
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
            <Footer />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PatientForm;
