import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import { QRCodeCanvas } from "qrcode.react";

function PatientDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    const fetchPatient = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4600/api/auth/patient-card/${id}`
        );
        setPatient(response.data);
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    };
    fetchPatient();
  }, [id]);

  const generatePatientReport = async (patient) => {};

  // Convert Image URL to Base64
  const getBase64Image = (url, maxWidth = 100, maxHeight = 100) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = "Anonymous";
      img.src = url;

      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");

        let width = img.width;
        let height = img.height;

        // Maintain aspect ratio
        if (width > maxWidth || height > maxHeight) {
          const aspectRatio = width / height;
          if (width > height) {
            width = maxWidth;
            height = maxWidth / aspectRatio;
          } else {
            height = maxHeight;
            width = maxHeight * aspectRatio;
          }
        }

        // Set high-resolution canvas
        canvas.width = width * 2; // Double resolution for better quality
        canvas.height = height * 2;
        ctx.scale(2, 2); // Scale for high-quality rendering

        // Enable image smoothing for better quality
        ctx.imageSmoothingEnabled = true;
        ctx.drawImage(img, 0, 0, width, height);

        resolve(canvas.toDataURL("image/jpeg", 1.0)); // Keep quality at 100%
      };

      img.onerror = reject;
    });
  };

  return (
    <div className="container mx-auto p-6 bg-gray-100 min-h-screen flex flex-col items-center">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl">
        <h2 className="text-2xl font-semibold mb-4 text-center">
          {patient?.name}'s Summary
        </h2>

        <div className="flex items-center justify-center mb-6">
          {patient?.profileImage && (
            <img
              src={`http://localhost:4600${patient?.profileImage}`}
              alt="Profile"
              className="w-24 h-24 rounded-full"
            />
          )}
        </div>

        <div className="mb-6 text-center">
          <p>
            <strong>Phone:</strong> {patient?.phoneNumber}
          </p>
          <p>
            <strong>Blood Type:</strong> {patient?.blood}
          </p>
          <p>
            <strong>Token:</strong> Token-{id}
          </p>
        </div>

        <div className="flex justify-center items-center flex-col">
          <p className="font-semibold">Scan QR for Full Details</p>
          <QRCodeCanvas
            value={`http://localhost:4600/patient-full-detail/${id}`}
            size={120}
          />
        </div>

        <button
          onClick={generatePatientReport}
          className="mt-6 px-4 py-2 bg-blue-500 text-white rounded w-full"
        >
          Generate Report
        </button>
        <button
          onClick={() => navigate(-1)}
          className="w-full mt-4 px-4 py-2 bg-gray-400 text-white rounded"
        >
          Back
        </button>
      </div>
    </div>
  );
}

export default PatientDetail;
