import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import { QRCodeCanvas } from "qrcode.react";

function PatientDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPatient = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4600/api/auth/patient-card/${id}`
        );
        setPatient(response.data);
      } catch (error) {
        console.error("Error fetching patient data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchPatient();
  }, [id]);

  const generatePatientReport = async () => {
    if (!patient) return;
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Clinic Name", 105, 15, null, null, "center");
    doc.setFontSize(12);
    doc.text(
      `Patient Report - ${new Date().toLocaleDateString()}`,
      105,
      22,
      null,
      null,
      "center"
    );

    let startY = 30;
    if (patient?.profileImage) {
      try {
        const imageUrl = `http://localhost:4600${patient?.profileImage}`;
        const imageData = await getBase64Image(imageUrl);
        doc.addImage(imageData, "JPEG", 80, startY, 50, 50);
        startY += 60;
      } catch (error) {
        console.error("Error loading image:", error);
      }
    }

    doc.autoTable({
      startY: startY + 10,
      head: [["Info", "Details"]],
      body: [
        ["Name", patient?.name],
        ["Phone Number", patient?.phoneNumber],
        ["Blood Type", patient?.blood],
        ["Token Number", `Token-${id}`],
      ],
      theme: "grid",
      headStyles: { fillColor: [22, 160, 133] },
      styles: { fontSize: 12, cellPadding: 5 },
    });

    doc.setFontSize(10);
    doc.text(
      "Â© 2025 Clinic Name | Confidential Report",
      105,
      doc.internal.pageSize.height - 10,
      null,
      null,
      "center"
    );

    doc.save(`Patient_Report_${patient?.name}.pdf`);
  };

  const getBase64Image = (url, maxWidth = 60, maxHeight = 60) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = "Anonymous";
      img.src = url;

      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");

        let width = img.width;
        let height = img.height;

        if (width > maxWidth || height > maxHeight) {
          const aspectRatio = width / height;
          if (width > height) {
            width = maxWidth;
            height = maxWidth / aspectRatio;
          } else {
            height = maxHeight;
            width = maxHeight * aspectRatio;
          }
        }

        canvas.width = width * 2;
        canvas.height = height * 2;
        ctx.scale(2, 2);

        ctx.imageSmoothingEnabled = true;
        ctx.drawImage(img, 0, 0, width, height);

        resolve(canvas.toDataURL("image/jpeg", 1.0));
      };

      img.onerror = reject;
    });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="text-lg font-semibold">Loading patient data...</div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      {/* Back Button */}
     

      <div className="flex flex-col items-center">
        {/* Patient Profile Image */}
        {patient?.profileImage && (
          <img
            src={`http://localhost:4600${patient?.profileImage}`}
            alt="Profile"
            className="w-32 h-32 rounded-full border-4 border-gray-200 shadow-md"
          />
        )}

        {/* Patient Details */}
        <h2 className="text-3xl font-bold mt-4">{patient?.name}</h2>
        <p className="text-gray-500">{patient?.phoneNumber}</p>

        <div className="mt-4">
          <p className="text-lg">
            <strong>Blood Type:</strong> {patient?.blood}
          </p>
          <p className="text-lg">
            <strong>Token Number:</strong> <span className="text-blue-500">Token-{id}</span>
          </p>
        </div>

        {/* QR Code & Report Button */}
        <div className="flex flex-col items-center mt-6">
          <p className="text-gray-600 font-semibold mb-2">Scan for Token</p>
          <QRCodeCanvas value={`Token-${id}`} size={120} />

          <button
            onClick={generatePatientReport}
            className="mt-4 px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white text-lg font-semibold rounded-md shadow"
          >
            ð Generate Report
          </button>

          <button
        onClick={() => navigate(-1)}
        className="mt-4 px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-md"
      >
        â¬ Back
      </button>
        </div>
      </div>
    </div>
  );
}

export default PatientDetail;
