import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

function PatientDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    const fetchPatient = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4600/api/auth/patient-card/${id}`
        );
        setPatient(response.data);
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    };
    fetchPatient();
  }, [id]);

  // ðŸ—‘ Delete Patient
  const handleDelete = async () => {
    if (!window.confirm("Are you sure you want to delete this patient?")) return;

    try {
      await axios.delete(`http://localhost:4600/api/auth/patient-card/${id}`);
      alert("Patient deleted successfully!");
      navigate("/patients"); // Redirect to patient list
    } catch (error) {
      console.error("Error deleting patient:", error);
      alert("Failed to delete patient.");
    }
  };

  return (
    <div className="container mx-auto p-6 bg-gray-100 min-h-screen flex flex-col items-center">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl">
        <h2 className="text-2xl font-semibold mb-4 text-center">
          {patient?.name}'s Details
        </h2>

        <div className="flex items-center justify-center mb-6">
          {patient?.profileImage && (
            <img
              src={`http://localhost:4600${patient?.profileImage}`}
              alt="Profile"
              className="w-24 h-24 rounded-full"
            />
          )}
        </div>

        <div className="mb-6 text-center">
          <p><strong>Phone:</strong> {patient?.phoneNumber}</p>
          <p><strong>Blood Type:</strong> {patient?.blood}</p>
          <p><strong>Token:</strong> Token-{id}</p>
        </div>

        {/* ðŸ›  Action Buttons */}
        <div className="flex gap-4 justify-center">
          <button
            onClick={() => navigate(`/patient-form/${id}`)} // Redirect to form
            className="px-4 py-2 bg-green-500 text-white rounded"
          >
            Edit
          </button>
          <button
            onClick={handleDelete}
            className="px-4 py-2 bg-red-500 text-white rounded"
          >
            Delete
          </button>
        </div>

        <button
          onClick={() => navigate(-1)}
          className="w-full mt-4 px-4 py-2 bg-gray-400 text-white rounded"
        >
          Back
        </button>
      </div>
    </div>
  );
}

export default PatientDetail;
