import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
// import QRCode from "qrcode.react";
import { QRCodeCanvas } from "qrcode.react";

function PatientDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    console.log(id);
    const fetchPatient = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4600/api/auth/patient-card/${id}`
        );
       

        setPatient(response.data);
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    };

    fetchPatient();
  }, [id]);

  const generatePatientReport = async () => {
    if (!patient) return;
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Clinic Name", 105, 15, null, null, "center");
    doc.setFontSize(12);
    doc.text(
      `Patient Report - ${new Date().toLocaleDateString()}`,
      105,
      22,
      null,
      null,
      "center"
    );

    let startY = 30;
    if (patient.profileImage) {
      try {
        const imageUrl = `http://localhost:4600${patient.profileImage}`;
        const imageData = await getBase64Image(imageUrl);
        doc.addImage(imageData, "JPEG", 80, startY, 50, 50);
        startY += 60;
      } catch (error) {
        console.error("Error loading image:", error);
      }
    }

    doc.autoTable({
      startY: startY + 10,
      head: [["Info", "Details"]],
      body: [
        ["Name", patient.name],
        ["Phone Number", patient.phoneNumber],
        ["Blood Type", patient.blood],
        ["Token Number", `Token-${id}`],
      ],
      theme: "grid",
      headStyles: { fillColor: [22, 160, 133] },
      styles: { fontSize: 12, cellPadding: 5 },
    });

    doc.setFontSize(10);
    doc.text(
      "© 2025 Clinic Name | Confidential Report",
      105,
      doc.internal.pageSize.height - 10,
      null,
      null,
      "center"
    );

    doc.save(`Patient_Report_${patient.name}.pdf`);
  };

  const getBase64Image = (url, maxWidth = 60, maxHeight = 60) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = "Anonymous";
      img.src = url;

      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");

        let width = img.width;
        let height = img.height;

        if (width > maxWidth || height > maxHeight) {
          const aspectRatio = width / height;
          if (width > height) {
            width = maxWidth;
            height = maxWidth / aspectRatio;
          } else {
            height = maxHeight;
            width = maxHeight * aspectRatio;
          }
        }

        canvas.width = width * 2;
        canvas.height = height * 2;
        ctx.scale(2, 2);

        ctx.imageSmoothingEnabled = true;
        ctx.drawImage(img, 0, 0, width, height);

        resolve(canvas.toDataURL("image/jpeg", 1.0));
      };

      img.onerror = reject;
    });
  };

//   if (!patient) return <div>Loading...</div>;

  return (
    <div className="container mx-auto p-6">
        test
      <button
        onClick={() => navigate(-1)}
        className="mb-4 px-4 py-2 bg-gray-400 text-white rounded"
      >
        Back
      </button>

      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-2xl font-semibold mb-4">
          {patient.name}'s Details
        </h2>

        <div className="flex items-center mb-4">
          {patient.profileImage && (
            <img
              src={`http://localhost:4600${patient.profileImage}`}
              alt="Profile"
              className="w-24 h-24 rounded-full mr-4"
            />
          )}
          <div>
            <p>
              <strong>Phone Number:</strong> {patient.phoneNumber}
            </p>
            <p>
              <strong>Blood Type:</strong> {patient.blood}
            </p>
            <p>
              <strong>Token Number:</strong> Token-{id}
            </p>
          </div>
        </div>

        <div className="flex items-center">
          <div className="mr-6">
            <p className="font-semibold">QR Code (Scan for Token)</p>
            {/* <QRCode value={`Token-${id}`} size={100} /> */}
            <QRCodeCanvas value={`Token-${id}`} size={100} />
          </div>
          <button
            onClick={generatePatientReport}
            className="px-4 py-2 bg-blue-500 text-white rounded"
          >
            Generate Report
          </button>
        </div>
      </div>
    </div>
  );
}

export default PatientDetail;
