import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import { QRCodeCanvas } from "qrcode.react";

function PatientDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    const fetchPatient = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4600/api/auth/patient-card/${id}`
        );
        setPatient(response.data);
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    };
    fetchPatient();
  }, [id]);

  // Convert Image URL to Base64
  const getBase64Image = (url) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = "Anonymous"; 
      img.src = url;
      
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");

        canvas.width = img.width;
        canvas.height = img.height;

        ctx.drawImage(img, 0, 0);
        resolve(canvas.toDataURL("image/png"));
      };

      img.onerror = () => reject(new Error("Failed to load image"));
    });
  };

  const generatePatientReport = async () => {
    if (!patient) return;
  
    const doc = new jsPDF();
  
    // Title
    doc.setFontSize(18);
    doc.text("Clinic Name", 105, 15, null, null, "center");
    doc.setFontSize(12);
    doc.text(`Patient Report - ${new Date().toLocaleDateString()}`, 105, 22, null, null, "center");
  
    let startY = 30;
  
    // Add Patient Image to PDF
    if (patient?.profileImage) {
      try {
        const imageUrl = `http://localhost:4600${patient?.profileImage}`;
        const imageData = await getBase64Image(imageUrl);
        doc.addImage(imageData, "PNG", 80, startY, 50, 50); 
        startY += 60; // Move table down
      } catch (error) {
        console.error("Error loading image:", error);
      }
    }
  
    // Patient Details Table
    doc.autoTable({
      startY: startY + 10,
      head: [["Info", "Details"]],
      body: [
        ["Name", patient?.name],
        ["Phone Number", patient?.phoneNumber],
        ["Blood Type", patient?.blood],
        ["Token Number", `Token-${id}`],
      ],
      theme: "grid",
      headStyles: { fillColor: [22, 160, 133] },
      styles: { fontSize: 12, cellPadding: 5 },
    });
  
    // Generate QR Code for Patient Full Details
    try {
      const qrDataUrl = await QRCode.toDataURL(`http://localhost:4600/patient-full-detail/${id}`);
  
      // Add QR Code to PDF
      doc.addImage(qrDataUrl, "PNG", 80, doc.autoTable.previous.finalY + 10, 50, 50);
    } catch (error) {
      console.error("Error generating QR Code:", error);
    }
  
    // Save PDF
    doc.save(`Patient_Report_${patient?.name}.pdf`);
  };

  return (
    <div className="container mx-auto p-6 bg-gray-100 min-h-screen flex flex-col items-center">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl">
        <h2 className="text-2xl font-semibold mb-4 text-center">
          {patient?.name}'s Summary
        </h2>

        <div className="flex items-center justify-center mb-6">
          {patient?.profileImage && (
            <img
              src={`http://localhost:4600${patient?.profileImage}`}
              alt="Profile"
              className="w-24 h-24 rounded-full"
            />
          )}
        </div>

        <div className="mb-6 text-center">
          <p><strong>Phone:</strong> {patient?.phoneNumber}</p>
          <p><strong>Blood Type:</strong> {patient?.blood}</p>
          <p><strong>Token:</strong> Token-{id}</p>
        </div>

        <div className="flex justify-center items-center flex-col">
          <p className="font-semibold">Scan QR for Full Details</p>
          <QRCodeCanvas value={`http://localhost:4600/patient-full-detail/${id}`} size={120} />
        </div>

        <button
          onClick={generatePatientReport}
          className="mt-6 px-4 py-2 bg-blue-500 text-white rounded w-full"
        >
          Generate Report
        </button>
        <button
          onClick={() => navigate(-1)}
          className="w-full mt-4 px-4 py-2 bg-gray-400 text-white rounded"
        >
          Back
        </button>
      </div>
    </div>
  );
}

export default PatientDetail;
